//
//  "watch" code for Mission13 mission
//
void Watch_Mission13(void)
{
	CurrentMissionScope = KAS_SCOPE_MISSION;
	strcpy(CurrentMissionScopeName, "Mission13");
	if (!kasfVarValueGet("_VIFONCE020") && ((sdword)kasfRenderedShips(kasGrowSelectionPtr("GoneShips"), 3))) // "ifonce" #20
	{
	kasfVarCreateSet("_VIFONCE020", 1);
	(sdword)(kasfMsgSendAll("FoundShip"), 0);
	}
	if (!kasfVarValueGet("_VIFONCE021") && (((sdword)kasfShipsCount(kasAITeamShipsPtr("JunkyardHQ")) < 1) && ((sdword)kasfObjectiveGet("EstablishCommunicationsLink") == 0))) // "ifonce" #21
	{
	kasfVarCreateSet("_VIFONCE021", 1);
	(sdword)(kasfMissionFailed(), 0);
	}
	if (!kasfVarValueGet("_VIFONCE022") && ((sdword)kasfShipsCount(kasAITeamShipsPtr("Defector")) < 1)) // "ifonce" #22
	{
	kasfVarCreateSet("_VIFONCE022", 1);
	(sdword)(kasfMissionFailed(), 0);
	}
	if ((sdword)kasfVarValueGet("G_GunTimer") == 0)
	{
	(sdword)(kasfVarValueSet("G_GunTimer", 1), 0);
	}
	else
	{
	(sdword)(kasfVarValueSet("G_GunTimer", 0), 0);
	}
	if (!kasfVarValueGet("_VIFONCE023") && ((sdword)kasfTimerExpiredDestroy("BigGunTimer"))) // "ifonce" #23
	{
	kasfVarCreateSet("_VIFONCE023", 1);
	(sdword)(kasfAllowPlayerToResearch("HeavyGuns"), 0);
	(sdword)(kasfMsgSendAll("BigGuns"), 0);
	(sdword)(kasfTimerCreateSetStart("CloakingTimer", ((sdword)kasfRandom(90, 240))), 0);
	}
	if (!kasfVarValueGet("_VIFONCE024") && ((sdword)kasfTimerExpiredDestroy("CloakingTimer"))) // "ifonce" #24
	{
	kasfVarCreateSet("_VIFONCE024", 1);
	(sdword)(kasfAllowPlayerToResearch("CloakGeneratorTech"), 0);
	(sdword)(kasfMsgSendAll("CloakingTech"), 0);
	}
	if (!kasfVarValueGet("_VIFONCE025") && ((sdword)kasfDoesPlayerHave("MassDrive10Kt"))) // "ifonce" #25
	{
	kasfVarCreateSet("_VIFONCE025", 1);
	(sdword)(kasfAllowPlayerToResearch("Chassis2"), 0);
	}
	if (((sdword)kasfDoesPlayerHave("Chassis2")) && ((sdword)kasfVarValueGet("SetMediumGuns") == 0))
	{
	(sdword)(kasfAllowPlayerToResearch("MediumGuns"), 0);
	(sdword)(kasfVarCreateSet("SetMediumGuns", 1), 0);
	}
	if (!kasfVarValueGet("_VIFONCE026") && ((sdword)kasfDoesPlayerHave("Chassis1"))) // "ifonce" #26
	{
	kasfVarCreateSet("_VIFONCE026", 1);
	(sdword)(kasfAllowPlayerToResearch("MassDrive10Kt"), 0);
	}
	
}


